        [Theory]
        [MemberData(nameof(KnownKeys))]
        public void TestVerifyKnownKeys(MemeKeyIndex keyIndex, byte[] key)
        {
            MemeCrypto.VerifyMemeData(key, out var _, keyIndex).Should().BeTrue("becuase they key should be valid");
        }
